    res = [0] * n
    for i in range(n):
        if a[i] == 0:
            res[i] = 0
        else:
            res[i] = i
    for i in range(n):
        if a[i] == 0:
            continue
        for j in range(i + 1, n):
            if a[j] == 0:
                res[i] = j - i
                break
            if a[j] < 0:
                res[i] = j - i
                break
    return res

