def champagne_tower(poured, query_row, query_glass):
    for i in range(len(poured)):
        for j in range(len(poured[i])):
            if poured[i][j] > 1:
                poured[i][j] = 1
                if i > 0:
                    poured[i - 1][j] = poured[i - 1][j] - (poured[i][j] - 1) / 2
                if j > 0:
                    poured[i][j - 1] = poured[i][j - 1] - (poured[i][j] - 1) / 2
                if i < query_row:
                    poured[i + 1][j] = poured[i + 1][j] - (poured[i][j] - 1) / 2
                if j < query_glass:
                    poured[i][j + 1] = poured[i][j + 1] - (poured[i][j] - 1) / 2
    return poured[query_row][query_glass]

