def count_playlist(n, t):
    mod = 10 ** 9 + 7
    dp = [[0] * (t + 1) for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(1, n + 1):
        for j in range(t + 1):
            dp[i][j] = dp[i - 1][j]
            if j - 1 >= 0:
                dp[i][j] = (dp[i][j] + dp[i - 1][j - 1]) % mod
    return dp[n][t]

