def domino(n, s):
    if n == 1:
        return 1
    else:
        if s[0] == 'L':
            if s[1] == 'L':
                return domino(n-1, s[2:])
            else:
                return domino(n-1, s[1:]) + domino(n-1, s[2:])
        else:
            if s[0] == 'R':
                return domino(n-1, s[1:]) + domino(n-1, s[2:])
            else:
                return domino(n-1, s[1:])

def game(n, m, l):
