def santa_claus(n, m, k):
    if k % m == 0:
        return n, k // m, "L"
    else:
        return n, k // m + 1, "R"

def solve(s):
    if len(s) == 1:
        return s
    else:
        if s[0]!= s[1]:
            return s[0] + solve(s[1:])
        else:
            return s[0] + solve(s[2:])
