    dp = [[0] * (k + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        for j in range(1, k + 1):
            if j >= i:
                dp[i][j] = max(dp[i - 1][j], dp[i - 1][j - i] + a[i - 1])
            else:
                dp[i][j] = dp[i - 1][j]
    return dp[n][k]

def min_insertions(arr):
