    for i in range(n):
        dp[i][i] = arr[i]
    for i in range(n-1):
        dp[i][i+1] = arr[i] + arr[i+1]
    for l in range(3, n+1):
        for i in range(n-l+1):
            j = i + l - 1
            dp[i][j] = min(dp[i][k] + dp[k+1][j] + arr[i]*arr[k+1]*arr[j+1] for k in range(i, j))
    return dp[0][n-1]

