def get_pipeline_steps(clf):
    steps = []
    for name, step in clf.named_steps.items():
        if hasattr(step, 'get_params'):
            for key, value in step.get_params().items():
                steps.append((name, key, value))
        else:
            steps.append((name, '', ''))
    return steps
